return [{
  json: {
    sheet_id: $node["Loop Over Items"].json.Id,  // adjust column name
    image_url_1: items[0]?.json.imageUrl || '',
    image_url_2: items[1]?.json.imageUrl || '',
    image_url_3: items[2]?.json.imageUrl || '',
  }
}];

return items.map(item => ({
  json: {
    id: item.json.id,
    name: item.json.name,
    imageUrl: `https://drive.google.com/uc?id=${item.json.id}`
  }
}));

// Filter image files by filename (since mimeType is not present)
const images = items.filter(item =>
  item.json.name &&
  item.json.name.match(/\.(jpg|jpeg|png|webp|gif)$/i)
);

// Safety check
if (images.length === 0) {
  return [];
}

// Shuffle helper
function shuffle(arr) {
  return arr.sort(() => 0.5 - Math.random());
}

// Pick 3 random images
const selected = shuffle(images).slice(0, 3);

// Return in proper n8n format
return selected.map(item => ({
  json: item.json
}));
